import React from "react";
import { useNavigate } from "react-router-dom";
import FullCalendar from "@fullcalendar/react";
import listPlugin from "@fullcalendar/list"; // Import the list plugin
import dayGridPlugin from "@fullcalendar/daygrid";
import timeGridPlugin from "@fullcalendar/timegrid";
import interactionPlugin from "@fullcalendar/interaction"; // Enables interactivity like clicking or dragging events

function CalView() {
    const navigate = useNavigate();

    // Example events to display on the calendar
    const events = [
        { title: "Meeting with Team", date: "2024-11-06" },
        { title: "Project Deadline", date: "2024-11-06" },
        { title: "Workshop", start: "2024-11-12T10:00:00", end: "2024-11-12T12:00:00" },
    ];

    // Handle navigating back based on user type
    const handleBackToHome = () => {
        const userType = localStorage.getItem("type");

        if (userType) {
            if (userType === "Org") {
                navigate("/dashboard");
            } else if (userType === "CDMO" || userType === "OSAAR") {
                navigate("/admindashboard");
            } else {
                alert("User type not recognized.");
            }
        } else {
            alert("User type not set in localStorage.");
        }
    };

    return (
        <div>
            <h1>Calendar View</h1>
            <div style={{ maxHeight: "600px", overflowY: "scroll" }}>
                <FullCalendar
                    plugins={[listPlugin, dayGridPlugin, timeGridPlugin, interactionPlugin]}
                    initialView="listMonth" // Use the list month view for scrolling downwards
                    editable={true} // Allow dragging and resizing events
                    selectable={true} // Allow users to select dates/times
                    events={events} // Set initial events
                    dateClick={(info) => alert(`Clicked on date: ${info.dateStr}`)} // Handle date clicks
                    eventClick={(info) => alert(`Event: ${info.event.title}`)} // Handle event clicks
                    height="auto" // Automatically adjust the height based on content
                />
            </div>
            <button onClick={handleBackToHome}>Back to Dashboard</button>
        </div>
    );
}

export default CalView;
