import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

function Home() {
    const navigate = useNavigate();

    const handleChoice = (choice) => {
        switch (choice) {
            case "1":
                navigate("/signin");
                break;
            case "2":
                navigate("/login");
                break;
            case "3":
                alert("Exiting program...");
                break;
            default:
                alert("Invalid choice. Please try again.");
                break;
        }
    };

    return (
        <div style={{ backgroundColor: 'red' }}>
            <h1>Connect</h1>
            <button onClick={() => handleChoice("1")}>Sign Up</button>
            <button onClick={() => handleChoice("2")}>Log In</button>
            <button onClick={() => handleChoice("3")}>Exit</button>
        </div>
    );
}

export default Home;
