import React, { useState, useEffect } from "react";
import { useNavigate, useParams } from "react-router-dom";
import axios from "axios";

function UpdateEvent() {
    const navigate = useNavigate();
    const { eventName: paramEventName } = useParams();

    // State to track form field values
    const [eventName, setEventName] = useState("");
    const [eventDate, setEventDate] = useState("");
    const [eventVenue, setEventVenue] = useState("");
    const [eventObjectives, setEventObjectives] = useState("");
    const [relevantSDGs, setRelevantSDGs] = useState("");
    const [username, setUsername] = useState("");

    useEffect(() => {
        // Retrieve the username from localStorage
        const storedUsername = localStorage.getItem("username");
        if (storedUsername) {
            setUsername(storedUsername);
        }

        // Fetch the event data from the backend
        const fetchEventData = async () => {
            try {
                const response = await axios.get(`http://localhost:8080/events/update/${paramEventName}`);
                if (response.data.success) {
                    const event = response.data.event;
                    setEventName(event.eventName);
                    setEventDate(event.eventDate);
                    setEventVenue(event.eventVenue);
                    setEventObjectives(event.eventObjectives);
                    setRelevantSDGs(event.eventSDGs);
                } else {
                    alert("Event not found. Please try again.");
                    navigate("/dashboard");
                }
            } catch (error) {
                console.error("Error fetching event data:", error);
                alert("An error occurred while fetching the event data. Please try again.");
            }
        };

        fetchEventData();
    }, [paramEventName, navigate]);

    const handleSubmit = async () => {
        // Create an event object to send to the backend
        const updatedEvent = {
            eventName,
            eventDate,
            eventVenue,
            eventObjectives,
            eventSDGs: relevantSDGs,
        };

        try {
            // Send a PUT request to the backend to update the event
            const response = await axios.put(`http://localhost:8080/events/update/${paramEventName}`, updatedEvent);

            if (response.data.success) {
                alert("Event updated successfully!");
                navigate("/dashboard"); // Redirect to dashboard after successful update
            } else {
                alert("Failed to update event. Please try again.");
            }
        } catch (error) {
            console.error("Error updating event:", error);
            alert("An error occurred while updating the event. Please try again.");
        }
    };

    return (
        <div>
            <h1>Update Event</h1>

            {/* Form Fields for Event Details */}
            <div style={{ marginBottom: "20px" }}>
                <div style={{ marginBottom: "10px" }}>
                    <label>Event Name:</label>
                    <input
                        type="text"
                        value={eventName}
                        onChange={(e) => setEventName(e.target.value)}
                        placeholder="Enter event name"
                        style={{ marginLeft: "10px" }}
                    />
                </div>
                <div style={{ marginBottom: "10px" }}>
                    <label>Event Date (MM-DD-YYYY):</label>
                    <input
                        type="text"
                        value={eventDate}
                        onChange={(e) => setEventDate(e.target.value)}
                        placeholder="MM-DD-YYYY"
                        style={{ marginLeft: "10px" }}
                    />
                </div>
                <div style={{ marginBottom: "10px" }}>
                    <label htmlFor="eventVenue">Select Venue:</label>
                    <select
                        id="eventVenue"
                        value={eventVenue}
                        onChange={(e) => setEventVenue(e.target.value)}
                    >
                        <option value="">--Choose an option--</option>
                        <option value="Seminar Room">Seminar Room</option>
                        <option value="Smart Room">Smart Room</option>
                        <option value="AVR 1">AVR 1</option>
                        <option value="AVR 2">AVR 2</option>
                    </select>
                </div>
                <div style={{ marginBottom: "10px" }}>
                    <label>Event Objectives:</label>
                    <textarea
                        value={eventObjectives}
                        onChange={(e) => setEventObjectives(e.target.value)}
                        placeholder="Enter event objectives"
                        style={{ marginLeft: "10px", width: "300px", height: "100px" }}
                    />
                </div>
                <div style={{ marginBottom: "10px" }}>
                    <label>Relevant SDGs (separated by commas):</label>
                    <input
                        type="text"
                        value={relevantSDGs}
                        onChange={(e) => setRelevantSDGs(e.target.value)}
                        placeholder="1,2, etc."
                        style={{ marginLeft: "10px" }}
                    />
                </div>

                {/* Submit Button */}
                <button onClick={handleSubmit}>Update Event</button>
            </div>

            {/* Back to Dashboard Button */}
            <button onClick={() => navigate("/dashboard")}>Back to Dashboard</button>
        </div>
    );
}

export default UpdateEvent;
