import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import bannerImage from "./CONNECT Site Banner.png";
import "./DashboardPage.css";

function DashboardPage() {
    const navigate = useNavigate();
    const [username, setUsername] = useState("");
    const [hoveredButton, setHoveredButton] = useState(null);

    const handleMouseEnter = (buttonName) => {
        setHoveredButton(buttonName);
    };

    const handleMouseLeave = () => {
        setHoveredButton(null);
    };

    useEffect(() => {
        const storedUsername = localStorage.getItem("username");
        if (storedUsername) {
            setUsername(storedUsername);
        }
    }, []);

    return (
        <div>
            {/* Image Banner */}
            <div
                style={{
                    width: "100%",
                    height: "500px",
                    overflow: "hidden",
                    marginBottom: "0px",
                }}
            >
                <img
                    src={bannerImage} 
                    alt="Banner"
                    style={{
                        width: "100%",
                        height: "100%",
                        objectFit: "cover",
                    }}
                />
            </div>

            {/* Yellow Background Container */}
            <div style={{ backgroundColor: "yellow", paddingBottom: "20px" }}>
                {/* Navigation Bar */}
                <div
                    style={{
                        display: "flex",
                        backgroundColor: "red",
                        width: "100%",
                        height: "60px",
                        alignItems: "center",
                    }}
                >
                    <button
                        style={{
                            flex: 1,
                            height: "100%",
                            backgroundColor: hoveredButton === "Calendar" ? "crimson" : "darkred",
                            color: "white",
                            border: "none",
                            fontSize: "18px",
                            cursor: "pointer",
                            transition: "background-color 0.3s ease",
                        }}
                        onMouseEnter={() => handleMouseEnter("Calendar")}
                        onMouseLeave={handleMouseLeave}
                        onClick={() => navigate("/calview")}
                    >
                        Calendar View
                    </button>
                    <button
                        style={{
                            flex: 1,
                            height: "100%",
                            backgroundColor: hoveredButton === "AddEvent" ? "crimson" : "darkred",
                            color: "white",
                            border: "none",
                            fontSize: "18px",
                            cursor: "pointer",
                            transition: "background-color 0.3s ease",
                        }}
                        onMouseEnter={() => handleMouseEnter("AddEvent")}
                        onMouseLeave={handleMouseLeave}
                        onClick={() => navigate("/addevent")}
                    >
                        Add Event
                    </button>
                    <button
                        style={{
                            flex: 1,
                            height: "100%",
                            backgroundColor: hoveredButton === "SignOut" ? "crimson" : "darkred",
                            color: "white",
                            border: "none",
                            fontSize: "18px",
                            cursor: "pointer",
                            transition: "background-color 0.3s ease",
                        }}
                        onMouseEnter={() => handleMouseEnter("SignOut")}
                        onMouseLeave={handleMouseLeave}
                        onClick={() => navigate("/")}
                    >
                        Sign Out
                    </button>
                </div>

                {/* Main Dashboard Content */}
                <div style={{ padding: "20px" }}>
                    <button onClick={() => navigate("/updateevent")}>Go to Update Event</button>
                    <button onClick={() => navigate("/")}>Back to Home</button>
                </div>
            </div>
        </div>
    );
}

export default DashboardPage;
