import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

function SignUp() {
    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");
    const [type, setType] = useState(""); // Track login type (Org, CDMO, OSAAR)
    const navigate = useNavigate();

    const handleSignUp = async () => {
        try {
            const response = await axios.post("http://localhost:8080/signup", { username, password, type });

            // Check different response messages and show alerts
            if (response.data.takenUser) {
                alert("Username is already taken");
            } else if (response.data.invalidPass) {
                alert("Password does not meet security requirements");
            } else if (response.data.success) {
                alert("Sign up successful");
                navigate("/"); // Redirect to the home page or wherever you want after successful signup
            } else {
                alert("An unknown error occurred during signup");
            }
        } catch (error) {
            console.error("Sign up error:", error);
            alert("An error occurred during sign up. Please try again.");
        }
    };

    return (
        <div>
            <h1>Sign Up</h1>
            
            {/* Dropdown to select type */}
            <label htmlFor="signupType">Select Type:</label>
            <select
                id="signupType"
                value={type}
                onChange={(e) => setType(e.target.value)}
            >
                <option value="">--Choose an option--</option>
                <option value="Org">Organization</option>
                <option value="CDMO">CDMO</option>
                <option value="OSAAR">OSAAR</option>
            </select>

            {/* Sign up form fields */}
            <div>
                <input
                    type="text"
                    placeholder="Username"
                    value={username}
                    onChange={(e) => setUsername(e.target.value)}
                />
                <input
                    type="password"
                    placeholder="Password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                />
                <button onClick={handleSignUp}>Sign Up</button>
            </div>

            <button onClick={() => navigate("/")}>Back to Home</button>
        </div>
    );
}

export default SignUp;
