import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

function Home() {
    const [inputText, setInputText] = useState("");
    const navigate = useNavigate();

    const handleButtonClick = async () => {
        try {
            const response = await axios.post("http://localhost:8080/process-text", { text: inputText }, {
                headers: {
                    "Content-Type": "application/json"
                }
            });
            const processedText = response.data.processedText;
            navigate("/hello", { state: { message: processedText } });
        } catch (error) {
            console.error("Error processing text:", error);
            if (error.response) {
                console.error("Backend response:", error.response.data);
            }
        }
    };

    return (
        <div>
            <h1>Welcome</h1>
            <input 
                type="text" 
                value={inputText} 
                onChange={(e) => setInputText(e.target.value)} 
                placeholder="Enter text to process"
            />
            <button onClick={handleButtonClick}>Process and Go to Hello Page</button>
            <button onClick={() => navigate("/signin")}>Go to Sign In</button>
            <button onClick={() => navigate("/dashboard")}>Go to Dashboard</button>
        </div>
    );
}

export default Home;
