import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

function LogIn() {
    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");
    const [showOrgFields, setShowOrgFields] = useState(false); // Track if Org fields should show
    const navigate = useNavigate();

    const handleLogin = async () => {
        try {
            const response = await axios.post("http://localhost:8080/login", { username, password });
            if (response.data.success) {
                navigate("/dashboard"); // Redirect to dashboard on success
            } else {
                alert("Invalid credentials. Please try again."); // Show error message on failure
            }
        } catch (error) {
            console.error("Login error:", error);
            alert("An error occurred during login. Please try again.");
        }
    };

    return (
        <div>
            <h1>Log In</h1>

            {/* Display buttons for Org, CDMO, and OSAAR */}
            {!showOrgFields && (
                <div>
                    <button onClick={() => setShowOrgFields(true)}>Org</button>
                    <button onClick={() => alert("CDMO login is not yet implemented")}>CDMO</button>
                    <button onClick={() => alert("OSAAR login is not yet implemented")}>OSAAR</button>
                </div>
            )}

            {/* Show login fields only for Org selection */}
            {showOrgFields && (
                <div>
                    <input
                        type="text"
                        placeholder="Username"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                    />
                    <input
                        type="password"
                        placeholder="Password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                    />
                    <button onClick={handleLogin}>Go to Dashboard</button>
                </div>
            )}

            <button onClick={() => navigate("/")}>Back to Home</button>
        </div>
    );
}

export default LogIn;
