import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

function SignIn() {
    const [selectedOption, setSelectedOption] = useState(""); // State to track selected option
    const navigate = useNavigate();

    const handleOptionChange = (event) => {
        setSelectedOption(event.target.value);
    };

    const handleSubmit = () => {
        if (selectedOption) {
            alert(`You selected: ${selectedOption}`);
            // Perform further actions based on selection if needed
        } else {
            alert("Please select an option");
        }
    };

    return (
        <div>
            <h1>Sign In</h1>
            
            <label htmlFor="loginType">Select Type:</label>
            <select id="loginType" value={selectedOption} onChange={handleOptionChange}>
                <option value="">--Choose an option--</option>
                <option value="Organization">Organization</option>
                <option value="CDMO">CDMO</option>
                <option value="OSAAR">OSAAR</option>
            </select>

            <button onClick={handleSubmit}>Submit</button>
            <button onClick={() => navigate("/")}>Back to Home</button>
        </div>
    );
}

export default SignIn;
