import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

function AddEvent() {
    const navigate = useNavigate();

    // State to track form field values
    const [eventName, setEventName] = useState("");
    const [eventDate, setEventDate] = useState("");
    const [eventVenue, setEventVenue] = useState("");
    const [eventObjectives, setEventObjectives] = useState("");
    const [relevantSDGs, setRelevantSDGs] = useState("");

    const handleSubmit = () => {
        // Handling event creation logic (you can send this data to your backend or do further processing)
        const newEvent = {
            eventName,
            eventDate,
            eventVenue,
            eventObjectives,
            relevantSDGs,
        };
        console.log("Event Created:", newEvent);
        alert("Event created successfully!");
    };

    return (
        <div>
            <h1>Add Event</h1>

            {/* Form Fields for Event Details */}
            <div style={{ marginBottom: "20px" }}>
                <div style={{ marginBottom: "10px" }}>
                    <label>Event Name:</label>
                    <input
                        type="text"
                        value={eventName}
                        onChange={(e) => setEventName(e.target.value)}
                        placeholder="Enter event name"
                        style={{ marginLeft: "10px" }}
                    />
                </div>
                <div style={{ marginBottom: "10px" }}>
                    <label>Event Date (MM-DD-YYYY):</label>
                    <input
                        type="text"
                        value={eventDate}
                        onChange={(e) => setEventDate(e.target.value)}
                        placeholder="MM-DD-YYYY"
                        style={{ marginLeft: "10px" }}
                    />
                </div>
                <div style={{ marginBottom: "10px" }}>
                    <label>Event Venue:</label>
                    <input
                        type="text"
                        value={eventVenue}
                        onChange={(e) => setEventVenue(e.target.value)}
                        placeholder="Enter event venue"
                        style={{ marginLeft: "10px" }}
                    />
                </div>
                <div style={{ marginBottom: "10px" }}>
                    <label>Event Objectives:</label>
                    <textarea
                        value={eventObjectives}
                        onChange={(e) => setEventObjectives(e.target.value)}
                        placeholder="Enter event objectives"
                        style={{ marginLeft: "10px", width: "300px", height: "100px" }}
                    />
                </div>
                <div style={{ marginBottom: "10px" }}>
                    <label>Relevant SDGs (separated by commas):</label>
                    <input
                        type="text"
                        value={relevantSDGs}
                        onChange={(e) => setRelevantSDGs(e.target.value)}
                        placeholder="1,2,etc."
                        style={{ marginLeft: "10px" }}
                    />
                </div>

                {/* Submit Button */}
                <button onClick={handleSubmit}>Create Event</button>
            </div>

            {/* Back to Dashboard Button */}
            <button onClick={() => navigate("/dashboard")}>Back to Dashboard</button>
        </div>
    );
}

export default AddEvent;
