import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import bannerImage from "./CONNECT Site Banner.png";
import "./DashboardPage.css";

function DashboardPage() {
    const navigate = useNavigate();
    const [username, setUsername] = useState("");
    const [hoveredButton, setHoveredButton] = useState(null);
    const [events, setEvents] = useState([]);

    const handleMouseEnter = (buttonName) => {
        setHoveredButton(buttonName);
    };

    const handleMouseLeave = () => {
        setHoveredButton(null);
    };

    useEffect(() => {
        // Retrieve the username from localStorage
        const storedUsername = localStorage.getItem("username");
        if (storedUsername) {
            setUsername(storedUsername);

            // Fetch events for the current user
            axios
                .get(`http://localhost:8080/events/${storedUsername}`)
                .then((response) => {
                    setEvents(response.data.events);
                })
                .catch((error) => {
                    console.error("Error fetching events:", error);
                });
        }
    }, []);

    // Event handler for deleting an event
    const handleDeleteEvent = (eventName) => {
        alert(`Are you sure you want to delete the event "${eventName}"?`);
        // Here, you can implement the actual delete logic if needed
    };

    // Event handler for updating an event
    const handleUpdateEvent = (eventName) => {
        alert(`You have chosen to update the event "${eventName}".`);
        // Here, you can implement the actual update logic if needed
    };

    return (
        <div>
            {/* Image Banner */}
            <div
                style={{
                    width: "100%",
                    height: "500px",
                    overflow: "hidden",
                    marginBottom: "0px",
                }}
            >
                <img
                    src={bannerImage} // Replace this URL with your image URL
                    alt="Banner"
                    style={{
                        width: "100%",
                        height: "100%",
                        objectFit: "cover",
                    }}
                />
            </div>

            {/* Navigation Bar */}
            <div
                style={{
                    display: "flex",
                    backgroundColor: "red",
                    width: "100%",
                    height: "60px",
                    alignItems: "center",
                }}
            >
                <button
                    style={{
                        flex: 1,
                        height: "100%",
                        backgroundColor: hoveredButton === "Calendar" ? "crimson" : "darkred",
                        color: "white",
                        border: "none",
                        fontSize: "18px",
                        cursor: "pointer",
                        transition: "background-color 0.3s ease",
                    }}
                    onMouseEnter={() => handleMouseEnter("Calendar")}
                    onMouseLeave={handleMouseLeave}
                    onClick={() => navigate("/calview")}
                >
                    Calendar View
                </button>
                <button
                    style={{
                        flex: 1,
                        height: "100%",
                        backgroundColor: hoveredButton === "AddEvent" ? "crimson" : "darkred",
                        color: "white",
                        border: "none",
                        fontSize: "18px",
                        cursor: "pointer",
                        transition: "background-color 0.3s ease",
                    }}
                    onMouseEnter={() => handleMouseEnter("AddEvent")}
                    onMouseLeave={handleMouseLeave}
                    onClick={() => navigate("/addevent")}
                >
                    Add Event
                </button>
                <button
                    style={{
                        flex: 1,
                        height: "100%",
                        backgroundColor: hoveredButton === "SignOut" ? "crimson" : "darkred",
                        color: "white",
                        border: "none",
                        fontSize: "18px",
                        cursor: "pointer",
                        transition: "background-color 0.3s ease",
                    }}
                    onMouseEnter={() => handleMouseEnter("SignOut")}
                    onMouseLeave={handleMouseLeave}
                    onClick={() => navigate("/")}
                >
                    Sign Out
                </button>
            </div>

            {/* Main Dashboard Content */}
            <div style={{ backgroundColor: "yellow", padding: "20px" }}>
                <h2>Welcome, {username}</h2>
                <h3>Your Events:</h3>
                <div style={{ backgroundColor: "white", padding: "20px", borderRadius: "10px" }}>
                    {events.length > 0 ? (
                        <ul style={{ listStyleType: "none", padding: 0, margin: 0 }}>
                            {events.map((event, index) => (
                                <li key={index} style={{ marginBottom: "15px", padding: "10px", border: "1px solid #ccc" }}>
                                    <strong>{event.eventName}</strong>
                                    <p>{event.eventObjectives}</p>
                                    <div style={{ display: "flex", justifyContent: "flex-end" }}>
                                        <button
                                            onClick={() => handleUpdateEvent(event.eventName)}
                                            style={{
                                                marginRight: "10px",
                                                padding: "5px 10px",
                                                cursor: "pointer",
                                            }}
                                        >
                                            Update
                                        </button>
                                        <button
                                            onClick={() => handleDeleteEvent(event.eventName)}
                                            style={{
                                                padding: "5px 10px",
                                                cursor: "pointer",
                                                backgroundColor: "red",
                                                color: "white",
                                                border: "none",
                                            }}
                                        >
                                            Delete
                                        </button>
                                    </div>
                                </li>
                            ))}
                        </ul>
                    ) : (
                        <p>No events available</p>
                    )}
                </div>
                <button onClick={() => navigate("/updateevent")}>Go to Update Event</button>
                <button onClick={() => navigate("/")}>Back to Home</button>
            </div>
        </div>
    );
}

export default DashboardPage;
