// frontend/src/Home.js
import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

function Home() {
    const [inputText, setInputText] = useState(""); // State to store input text
    const navigate = useNavigate();

    const handleButtonClick = () => {
        // Navigate to HelloPage and pass inputText as state
        navigate("/hello", { state: { message: inputText } });
    };

    return (
        <div>
            <h1>Welcome</h1>
            <input 
                type="text" 
                value={inputText} 
                onChange={(e) => setInputText(e.target.value)} 
                placeholder="Enter text to pass"
            />
            <button onClick={handleButtonClick}>Go to Hello Page</button>
        </div>
    );
}

export default Home;
