import FullCalendar from "@fullcalendar/react";
import dayGridPlugin from "@fullcalendar/daygrid";
import listPlugin from "@fullcalendar/list"; // This is how you should include the list view

function CalView() {
    return (
        <div>
            <h1>Calendar View</h1>
            <FullCalendar
                plugins={[dayGridPlugin, listPlugin]} // Add listPlugin here
                initialView="listMonth" // Set the initial view to listMonth
                editable={true}
                selectable={true}
                events={[
                    { title: "Meeting with Team", date: "2024-11-06" },
                    { title: "Project Deadline", date: "2024-11-06" },
                    { title: "Workshop", start: "2024-11-12T10:00:00", end: "2024-11-12T12:00:00" },
                ]}
                dateClick={(info) => alert(`Clicked on date: ${info.dateStr}`)}
                eventClick={(info) => alert(`Event: ${info.event.title}`)}
            />
            <button onClick={() => window.history.back()}>Back to Dashboard</button>
        </div>
    );
}

export default CalView;
