import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import bannerImage from "./CONNECT Site Banner.png";
import "./DashboardPage.css";

function DashboardPage() {
    const navigate = useNavigate();
    const [username, setUsername] = useState("");
    const [hoveredButton, setHoveredButton] = useState(null);
    const [events, setEvents] = useState([]);

    const handleMouseEnter = (buttonName) => {
        setHoveredButton(buttonName);
    };

    const handleMouseLeave = () => {
        setHoveredButton(null);
    };

    useEffect(() => {
        // Retrieve the username from localStorage
        const storedUsername = localStorage.getItem("username");
        if (storedUsername) {
            setUsername(storedUsername);

            // Fetch events for the current user
            axios
                .get(`http://localhost:8080/events/${storedUsername}`)
                .then((response) => {
                    setEvents(response.data.events);
                })
                .catch((error) => {
                    console.error("Error fetching events:", error);
                });
        }
    }, []);

    // Event handler for deleting an event
    const handleDeleteEvent = (eventName) => {
        alert(`Are you sure you want to delete the event "${eventName}"?`);
        // Here, you can implement the actual delete logic if needed
    };

    // Event handler for updating an event
    const handleUpdateEvent = (eventName) => {
        alert(`You have chosen to update the event "${eventName}".`);
        // Here, you can implement the actual update logic if needed
    };

    return (
        <div>
            {/* Image Banner */}
            <div
                style={{
                    width: "100%",
                    height: "500px",
                    overflow: "hidden",
                    marginBottom: "0px",
                }}
            >
                <img
                    src={bannerImage} // Replace this URL with your image URL
                    alt="Banner"
                    style={{
                        width: "100%",
                        height: "100%",
                        objectFit: "cover",
                    }}
                />
            </div>

            {/* Navigation Bar */}
            <div
                style={{
                    display: "flex",
                    backgroundColor: "red",
                    width: "100%",
                    height: "60px",
                    alignItems: "center",
                }}
            >
                <button
                    style={{
                        flex: 1,
                        height: "100%",
                        backgroundColor: hoveredButton === "Calendar" ? "crimson" : "#ff4c4c",
                        color: "white",
                        border: "none",
                        fontSize: "18px",
                        cursor: "pointer",
                        transition: "background-color 0.3s ease",
                    }}
                    onMouseEnter={() => handleMouseEnter("Calendar")}
                    onMouseLeave={handleMouseLeave}
                    onClick={() => navigate("/calview")}
                >
                    Calendar View
                </button>
                <button
                    style={{
                        flex: 1,
                        height: "100%",
                        backgroundColor: hoveredButton === "AddEvent" ? "crimson" : "#ff4c4c",
                        color: "white",
                        border: "none",
                        fontSize: "18px",
                        cursor: "pointer",
                        transition: "background-color 0.3s ease",
                    }}
                    onMouseEnter={() => handleMouseEnter("AddEvent")}
                    onMouseLeave={handleMouseLeave}
                    onClick={() => navigate("/addevent")}
                >
                    Add Event
                </button>
                <button
                    style={{
                        flex: 1,
                        height: "100%",
                        backgroundColor: hoveredButton === "SignOut" ? "crimson" : "#ff4c4c",
                        color: "white",
                        border: "none",
                        fontSize: "18px",
                        cursor: "pointer",
                        transition: "background-color 0.3s ease",
                    }}
                    onMouseEnter={() => handleMouseEnter("SignOut")}
                    onMouseLeave={handleMouseLeave}
                    onClick={() => navigate("/")}
                >
                    Sign Out
                </button>
            </div>

            {/* Main Dashboard Content */}
            <div style={{ backgroundColor: "#ffde4d", padding: "20px" }}>
                <h2>Welcome, {username}</h2>
                <h3>Your Events:</h3>
                {events.length > 0 ? (
                    <ul style={{ listStyleType: "none", padding: 0, margin: 0 }}>
                        {events.map((event, index) => (
                            <li
                                key={index}
                                style={{
                                    marginBottom: "15px",
                                    padding: "20px",
                                    backgroundColor: "#ff4c4c",
                                    color: "white",
                                    borderRadius: "10px", // Rounded edges
                                    boxShadow: "0 4px 8px rgba(0, 0, 0, 0.2)", // Shadow effect
                                    display: "flex",
                                    flexDirection: "column",
                                    alignItems: "flex-start",
                                    position: "relative",
                                }}
                            >
                                <strong>{event.eventName}</strong>
                                <p>{event.eventObjectives}</p>
                                <div style={{ position: "absolute", bottom: "10px", right: "10px", display: "flex" }}>
                                    <button
                                        onClick={() => handleUpdateEvent(event.eventName)}
                                        style={{
                                            marginRight: "10px",
                                            padding: "5px 10px",
                                            cursor: "pointer",
                                            backgroundColor: "#4CAF50",
                                            color: "white",
                                            border: "none",
                                            borderRadius: "4px",
                                        }}
                                    >
                                        Update
                                    </button>
                                    <button
                                        onClick={() => handleDeleteEvent(event.eventName)}
                                        style={{
                                            padding: "5px 10px",
                                            cursor: "pointer",
                                            backgroundColor: "red",
                                            color: "white",
                                            border: "none",
                                            borderRadius: "4px",
                                        }}
                                    >
                                        Delete
                                    </button>
                                </div>
                            </li>
                        ))}
                    </ul>
                ) : (
                    <p>No events available</p>
                )}
                <button onClick={() => navigate("/updateevent")} style={{ margin: "10px" }}>
                    Go to Update Event
                </button>
                <button onClick={() => navigate("/")}>Back to Home</button>
            </div>
        </div>
    );
}

export default DashboardPage;
